//SPDX-License-Identifier: Unlicense
pragma solidity ^0.7.0;
pragma abicoder v2;

import "../libraries/LibSimpleStake.sol";

interface ISimpleStake {

    event eventClaimedFLD(
        address account,
        address token,
        uint256 claimAmounts
    );

    event eventUpdateRewardsToken(
        address account,
        address tokenAddress,
        uint256 rewards,
        uint startBlock,
        uint endBlock
    );

    event eventStaked(
        address account,
        address tokenAddress,
        uint256 amount,
        uint256 genRewards
    );

    function stake(
        address _tokenAddr,
        uint256 _amount
    )
        external
        returns (uint256 rewards);

    function updateRewardsToken(
        address _tokenAddr
    )
        external
        returns (uint256 rewards);

    function calculateRewardsToken(
        address _tokenAddr,
        address _staker
    )
        external view
        returns (uint256 rewards);

    function claimFLD(
        address _tokenAddr
    )
        external;

    function finalizeWithdraw(
        address _tokenAddr
    )
        external
        returns (bool);

    function clearStaked(
        address _tokenAddr,
        address[] memory _stakers
    )
        external
        returns (bool);

    function requestWithdraw(
        address _tokenAddr,
        uint256 _amount
    )
        external
        returns (bool);

    function cancelRequestWithdraw(
        address _tokenAddr
    )
        external
        returns (bool);

    function allowedTokens(
        address _tokenAddress
    )
        external
        view
        returns (LibSimpleStake.TokenInfo memory);

    function StakeOf(
        address _tokenAddress,
        address _account
    )
        external
        view
        returns (LibSimpleStake.StakingInfo memory);



}
