//SPDX-License-Identifier: Unlicense
pragma solidity ^0.7.0;
pragma abicoder v2;

import "./StakeSetting.sol";
import "../interfaces/IFLD.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IWETH.sol";
import "../interfaces/ISimpleStake.sol";
import "../libraries/LibSimpleStake.sol";
import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";

contract SimpleStake
    is
    StakeSetting,
    ISimpleStake,
    Initializable
{

    function initialize(address _fld, address _fldVault, uint256 _interestRate, uint256 _totalBlocks )
        external
        initializer
    {
        require(_fld != address(0));
        fld = IFLD(_fld);
        fldVault = IFLDVault(_fldVault);

        _setupRole(ADMIN_ROLE, msg.sender);
        changeAnnualInterestRateInfo(_interestRate, _totalBlocks);
    }

    function stake(
        address _tokenAddr,
        uint256 _amount,
        uint256 deadline,
        bytes memory signature
    )
        external
        isValidToken(_tokenAddr)
        returns (uint256 rewards)
    {
        require(_amount != 0);
        rewards = 0;
        if (_StakeMap[_tokenAddr][msg.sender].amount == 0) {
            LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][msg.sender];
            userStaking.amount = _amount;
            userStaking.startBlock = block.number;
        } else {
            rewards = _updateRewardsToken(_tokenAddr, msg.sender, _amount);
        }

        tokenTotalStaked[_tokenAddr] += _amount;
        IWETH(_tokenAddr).permit(msg.sender, address(this), _amount, deadline, signature);
        require(IERC20(_tokenAddr).transferFrom(msg.sender, address(this), _amount));
        emit eventStaked(msg.sender, _tokenAddr, _amount, rewards);
    }

    function stake(
        address _tokenAddr,
        uint256 _amount
    )
        external
        override
        isValidToken(_tokenAddr)
        returns (uint256 rewards)
    {

        require(_amount != 0);

        if (_StakeMap[_tokenAddr][msg.sender].amount == 0) {

            LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][msg.sender];
            userStaking.amount = _amount;
            userStaking.startBlock = block.number;
        } else {
            rewards = _updateRewardsToken(_tokenAddr, msg.sender, _amount);
        }
        tokenTotalStaked[_tokenAddr] += _amount;

        require(IERC20(_tokenAddr).transferFrom(msg.sender, address(this), _amount));

        emit eventStaked(msg.sender, _tokenAddr, _amount, rewards);
    }

    function updateRewardsToken(
        address _tokenAddr
    )
        external
        override
        returns (uint256 rewards)
    {
        return _updateRewardsToken(_tokenAddr, msg.sender, 0);
    }

    function calculateRewardsToken(
        address _tokenAddr,
        address _staker
    )
        external
        override
        view
        returns (uint256 rewards)
    {
        require(_staker != address(0)  );

        uint256 start = _StakeMap[_tokenAddr][_staker].startBlock;
        uint256 stakedAmount = _StakeMap[_tokenAddr][_staker].amount;

        if (_StakeMap[_tokenAddr][_staker].releaseAmount > 0) {
            stakedAmount -= _StakeMap[_tokenAddr][_staker].releaseAmount ;
        }

        uint256 interest = interestRatePerYear.interestRate;
        if (_allowedTokens[_tokenAddr].interestRate > 0) interest = _allowedTokens[_tokenAddr].interestRate;

        if (stakedAmount > 0 && start > 0 && start < block.number ) {
            rewards =
                stakedAmount
                * ((block.number - start) / interestRatePerYear.totalBlocks)
                * (interest / LibSimpleStake.DECIMAL);
        }
    }

    function claimFLD(
        address _tokenAddr
    )
        external
        override
        isValidToken(_tokenAddr)
    {
        require(msg.sender != address(0)
            && address(fld) != address(0)
            && _allowedTokens[_tokenAddr].fldExchangesWeight > 0);

        address receiver = msg.sender;
        LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][receiver];
        uint256 claimAmounts = userStaking.rewards * _allowedTokens[_tokenAddr].fldExchangesWeight;
        userStaking.rewards = 0;

        require(fldVault.claimFLD(receiver, claimAmounts));

        emit eventClaimedFLD(receiver, _tokenAddr, claimAmounts);
    }

    function finalizeWithdraw(
        address _tokenAddr
    )
        external
        override
        isValidToken(_tokenAddr)
        returns (bool)
    {

        require(
            _StakeMap[_tokenAddr][msg.sender].releaseAmount > 0
            && _StakeMap[_tokenAddr][msg.sender].requestedWithdrawal);
        require(block.timestamp > _StakeMap[_tokenAddr][msg.sender].releaseDate);

        _updateRewardsToken(_tokenAddr, msg.sender, 0);

        LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][msg.sender];
        uint256 amount = userStaking.releaseAmount;
        uint256 balanceToken = IERC20(_tokenAddr).balanceOf(address(this));
        require(balanceToken >= amount && userStaking.amount >= amount);

        userStaking.amount = userStaking.amount-amount;
        userStaking.requestedWithdrawal = false;
        userStaking.releaseDate = 0;
        userStaking.releaseAmount = 0;

        tokenTotalStaked[_tokenAddr] = tokenTotalStaked[_tokenAddr]-amount;
        require(IERC20(_tokenAddr).transfer(msg.sender, amount));

        return true;
    }

    function clearStaked(
        address _tokenAddr,
        address[] memory _stakers
    )
        external
        override
        isMediator(_tokenAddr)
        isValidToken(_tokenAddr)
        returns (bool)
    {

        require(_stakers.length > 0);
        uint256 balanceToken = IERC20(_tokenAddr).balanceOf(address(this));
        uint256 sumBalancesUsers = 0;
        uint i =0;
        for (i =0; i < _stakers.length; i++) {
            sumBalancesUsers += _StakeMap[_tokenAddr][_stakers[i]].amount;
            _updateRewardsToken(_tokenAddr, _stakers[i], 0);
        }
        require(balanceToken >= sumBalancesUsers);
        for (i = 0; i < _stakers.length; i++) {
            LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][_stakers[i]];
            uint256 amount = userStaking.amount;
            userStaking.amount = 0;
            require(IERC20(_tokenAddr).transfer(_stakers[i], amount));
        }
        return true;
    }

    function requestWithdraw(
        address _tokenAddr,
        uint256 _amount
    )
        external
        override
        isValidToken(_tokenAddr)
        returns (bool)
    {
        require(
            !_StakeMap[_tokenAddr][msg.sender].requestedWithdrawal
            && _StakeMap[_tokenAddr][msg.sender].amount >= _amount);

        LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][msg.sender];
        userStaking.requestedWithdrawal = true;
        userStaking.releaseDate = block.timestamp + WITHDRAWAL_DELAY_SECONDS;
        userStaking.releaseAmount = _amount;
        return true;
    }

    function cancelRequestWithdraw(
        address _tokenAddr
    )
        external
        override
        isValidToken(_tokenAddr)
        returns (bool)
    {
        require(
            _StakeMap[_tokenAddr][msg.sender].requestedWithdrawal == true ||
            _StakeMap[_tokenAddr][msg.sender].releaseAmount > 0);

        LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][msg.sender];
        userStaking.requestedWithdrawal = false;
        userStaking.releaseDate = 0;
        userStaking.releaseAmount = 0;
        return true;
    }


    function allowedTokens(
        address _tokenAddr
    )
        external
        view
        override
        returns (LibSimpleStake.TokenInfo memory)
    {
        return _allowedTokens[_tokenAddr];
    }

    function StakeOf(
        address _tokenAddr,
        address _account
    )
        external
        view
        override
        returns (LibSimpleStake.StakingInfo memory)
    {
        return _StakeMap[_tokenAddr][_account];
    }

    /**
    * public
    */



    /**
    * internal
    */

    function _updateRewardsToken(
        address _tokenAddr,
        address _staker,
        uint256 _amount
    )
        internal
        returns (uint256 rewards)
    {

        require(_staker != address(0)
            && _StakeMap[_tokenAddr][_staker].startBlock > 0
            && _StakeMap[_tokenAddr][_staker].startBlock < block.number
        );

        LibSimpleStake.StakingInfo storage userStaking = _StakeMap[_tokenAddr][_staker];

        uint256 start = userStaking.startBlock;
        uint256 stakedAmount = userStaking.amount;

        if (userStaking.releaseAmount > 0) {
            stakedAmount -= userStaking.releaseAmount ;
        }

        if (stakedAmount > 0) {

            uint256 interest = interestRatePerYear.interestRate;
            if (_allowedTokens[_tokenAddr].interestRate > 0) interest = _allowedTokens[_tokenAddr].interestRate;

            rewards =
                stakedAmount
                * ((block.number - start) / interestRatePerYear.totalBlocks)
                * (interest / LibSimpleStake.DECIMAL);

            userStaking.rewards += rewards;
        }

        if (_amount > 0) userStaking.amount += _amount;
        userStaking.startBlock = block.number;
        userStaking.rewardBlock = block.number;

        emit eventUpdateRewardsToken(_staker, _tokenAddr, rewards, start, block.number);
    }


    /**
    * private
    */


}
