//SPDX-License-Identifier: Unlicense
pragma solidity ^0.7.6;
pragma abicoder v2;

import "../interfaces/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";
import "../interfaces/IFLDVault.sol";

import "../libraries/LibTokenStake.sol";
import './StakeStorageSetting.sol';

/**
 * @title Stake
 * Investors can submit Ether or specific tokens to lock them in the contract and mine FLD tokens.
 * @dev
 */
contract Stake
    is
    StakeStorageSetting, Initializable
{

    event Staked(address indexed paytoken, address indexed purchaser, uint256 value, uint256 duration, uint256 amount);
    event Claimed(address indexed paytoken, address indexed purchaser, uint256 value, uint256 duration, uint256 amount);
    event Withdrawal(address indexed paytoken, address indexed from, address token, uint256 value);

    function initialize(
        uint256 _startTime,
        uint256 _salePeriodSeconds,
        uint256 _stakePeriodSeconds,
        uint256 _cap,
        address _token,
        address _paytoken
    )
        external
        initializer
    {
        require(_token != address(0));
        require(_cap > 0 );

        _initialize();
        _setRoleAdmin(ADMIN_ROLE, ADMIN_ROLE);
        _setupRole(ADMIN_ROLE, msg.sender);

        if(_token != address(0)) token = _token;
        paytoken = _paytoken;
        _changeStartEndTime(_startTime, _salePeriodSeconds, _stakePeriodSeconds);
        _changeCap(_cap);
        _setDefaultStakePeriod(60*60*24*14); // 2 weeks
    }

    receive() external payable {
      stakeETH(defaultStakePeriod);
    }

    function stakeETH(uint256 _period)
        public payable
        isValidToken(address(0))
    {
        stake(address(0), msg.value, _period);
    }

    function stake(address paytoken, uint256 amount, uint256 period)
        public
        isValidToken(paytoken)
    {
        require(amount > 0 && period > 0);

        _addUserStake(msg.sender, period, amount);

        totalStaked += amount;

        if(paytoken != address(0)){
            require(IERC20(token).transferFrom(msg.sender, address(this), amount));
        }

        _addSnapshot();
    }

    // 인덱스 구간에서 리워드 발생 금액 리턴, 리워드가 발생한 인덱스 리스트 리턴
    function availableUserReward(address user, uint256 indexStart, uint256 indexEnd)
        external
        returns (uint256 reward )
    {
        if(indexEnd == 0) indexEnd = userStakes[user].length-1;
        require(indexStart <= indexEnd && indexEnd < userStakes[user].length);
        reward = 0;
        uint256 tempReward = 0;
        uint256 i = indexStart;
        uint256[] memory applyIndexs;

        while(i <= indexEnd && gasleft() > 1000 ){
            tempReward = availableSnapshotRecords(user, i);
            if(tempReward > 0) {
                applyIndexs[applyIndexs.length] = i;
                reward += tempReward;
            }
            i++;
        }
    }

    // 특정 인덱스의 리워드 계산
    function availableSnapshotRecords(address user, uint256 index)
        public view
        returns (uint256 reward)
    {
        require(index < userStakes[user].length);

        LibTokenStake.userStake storage userRecord = userStakes[user][index];

        require(userRecord.startBlock < userRecord.endBlock && userRecord.stakeAmount > 0 );
        if(userRecord.claimBlock > userRecord.endBlock )  return 0;

        reward = 0;
        uint256  userStakeStart = userRecord.startBlock;
        uint256  userStakeEnd = userRecord.endBlock;

        //시작블록에서의 스테이킹 보유량 .
        //uint256 userStakeAmount = userRecord.stakeAmount;
        //uint256 userStakeLastClaim  = userRecord.claimBlock;

        LibTokenStake.StakedSnapshot storage snapshotStart = stakedSnapshots[userStakeStart];

        uint256 i = snapshotStart.index;
        // 시작블록에서의 총금액
        //uint256 startTotalBalance = snapshotStart.balanceOfToken;
        bool loopLogic = true;
        // 시작블록에서의 지분율
        uint256 endBlock = 0;
        uint256 startBlock = 0;
        startBlock = userRecord.startBlock;
        if(userRecord.claimBlock > userRecord.startBlock )  startBlock = userRecord.claimBlock;
        while(i < snapshotBlocks.length && loopLogic){
            i++;
            // 다음블록으로 넘어가는 시점.
            //LibTokenStake.StakedSnapshot storage snapshot = stakedSnapshots[snapshotBlocks[i]];
            endBlock = snapshotBlocks[i];
            if (userStakeEnd  < snapshotBlocks[i]) {
                // 이전블록과 현재 블록의 간격
                endBlock = userStakeEnd;
                loopLogic = false;
            }
            reward += rewardPerBlock
                * (userRecord.stakeAmount / stakedSnapshots[snapshotBlocks[i]].balanceOfToken)
                * (endBlock - startBlock);

            startBlock = endBlock ;
        }
    }

    function _addUserStake(
        address user,
        uint256 _periods,
        uint256 _amount
    ) internal {

        require(_periods  > secondsPerBlock && _amount > 0 );

        LibTokenStake.userStake memory stakeInfo = LibTokenStake.userStake(
            block.number,
            _periods / secondsPerBlock,
            _amount,
            0,
            0
        );

        userStakes[user].push(stakeInfo);
        userStakesLength[user] += 1;
    }

    function _addSnapshot() internal {

        if(stakedSnapshots[block.number].blockNumber == 0){
            snapshotBlocks.push(block.number);
        }

        uint256 balanceOfPaytoken = 0;

        if (paytoken == address(0)) {
            address payable self = address(uint160(address(this)));
            balanceOfPaytoken = self.balance;

        } else {
            balanceOfPaytoken = IERC20(paytoken).balanceOf(address(this));
        }

        LibTokenStake.StakedSnapshot memory Snapshot = LibTokenStake.StakedSnapshot(
            block.number,
            totalStaked,
            balanceOfPaytoken,
            snapshotBlocks.length-1
        );
        stakedSnapshots[block.number] = Snapshot;
    }

}

